(function(){
  var root = document.getElementById('mainContent') || document.body;
  function savedIsDark(){
    try{
      var v = localStorage.getItem('mode');
      if (!v) v = localStorage.getItem('theme');
      if (!v) v = localStorage.getItem('darkMode');
      if (!v) v = localStorage.getItem('dark');
      if (!v) return false;
      v = String(v).toLowerCase();
      return (v === 'dark' || v === 'darkmode' || v === 'on' || v === 'true' || v === '1');
    }catch(e){
      return false;
    }
  }
  var isDark = savedIsDark();
  if (root && root.classList) root.classList.toggle('dark', isDark);
  var cb = document.getElementById('dark');
  if (cb) cb.checked = isDark;
})();
