div.b:tag:
//
(function(){
  if (window.__MenuNewsDarkModeFixV8) return;
  window.__MenuNewsDarkModeFixV8 = true;

  function getRoot(){
    return document.getElementById('mainContent') || document.body;
  }

  function readPref(){
    try{
      var v = localStorage.getItem('mode');
      if (v){
        v = String(v).toLowerCase();
        if (v === 'darkmode' || v === 'dark') return true;
        if (v === 'lightmode' || v === 'light') return false;
      }
      var d = localStorage.getItem('dark');
      if (d){
        d = String(d).toLowerCase();
        if (d === 'true' || d === '1' || d === 'on') return true;
        if (d === 'false' || d === '0' || d === 'off') return false;
      }
      var dm = localStorage.getItem('darkMode');
      if (dm){
        dm = String(dm).toLowerCase();
        if (dm === 'true' || dm === '1' || dm === 'on') return true;
        if (dm === 'false' || dm === '0' || dm === 'off') return false;
      }
    }catch(e){console.error(e);}
    return false;
  }

  function savePref(isDark){
    try{
      localStorage.setItem('mode', isDark ? 'darkmode' : 'lightmode');
      localStorage.setItem('dark', isDark ? 'true' : 'false');
      localStorage.setItem('darkMode', isDark ? 'true' : 'false');
    }catch(e){console.error(e);}
  }

  function syncCheckboxes(isDark){
    var inputs = document.querySelectorAll('input.dark-mode');
    if (!inputs || !inputs.length) return;
    for (var i=0; i<inputs.length; i++){
      inputs[i].checked = !!isDark;
    }
  }

  function apply(isDark){
    var root = getRoot();
    if (root && root.classList) root.classList.toggle('dark', !!isDark);
    syncCheckboxes(!!isDark);
  }

  // Apply saved preference ASAP after jQuery is loaded (DOM may not be ready; that's okay)
  apply(readPref());

  function _matches(el, sel){
    if (!el || el.nodeType !== 1) return false;
    var fn = el.matches || el.msMatchesSelector || el.webkitMatchesSelector;
    return fn ? fn.call(el, sel) : false;
  }
  function _closest(el, sel){
    while (el && el.nodeType === 1){
      if (_matches(el, sel)) return el;
      el = el.parentElement;
    }
    return null;
  }

  // Click on the styled switch (span)  toggle the nearest checkbox
  document.addEventListener('click', function(ev){
    var t = ev.target;
    if (!t) return;

    // Support clicks on the switch itself or its children
    var sw = (t.closest ? t.closest('.dark-toggle') : _closest(t, '.dark-toggle'));
    if (!sw) return;

    ev.preventDefault();

    var cb = null;
    if (sw.parentElement) cb = sw.parentElement.querySelector('input.dark-mode');
    if (!cb) cb = document.querySelector('input.dark-mode');
    if (!cb) return;

    cb.checked = !cb.checked;
    apply(cb.checked);
    savePref(cb.checked);
  }, true);

  // Direct checkbox changes (keyboard / accessibility)
  document.addEventListener('change', function(ev){
    var el = ev.target;
    if (!el) return;
    if (el.classList && el.classList.contains('dark-mode')){
      apply(el.checked);
      savePref(el.checked);
    }
  }, true);
})();
