(function(){
  try {
    var isMobile = false;
    try { isMobile = !!(window.matchMedia && window.matchMedia('(max-width: 768px)').matches); } catch(e){console.error(e);}

    function isVisible(el){
      try { return !!(el && (el.offsetWidth || el.offsetHeight || (el.getClientRects && el.getClientRects().length))); }
      catch(e){ return false; }
    }

    function setHigh(img){
      if(!img) return;
      img.setAttribute('fetchpriority', 'high');
      if (!img.hasAttribute('loading')) img.setAttribute('loading', 'eager');
      if (!img.hasAttribute('decoding')) img.setAttribute('decoding', 'async');
    }

    function setLow(img){
      if(!img) return;
      // Only downgrade if it was forced high in markup.
      if (img.getAttribute('fetchpriority') === 'high') img.setAttribute('fetchpriority', 'low');
      if (!img.hasAttribute('loading')) img.setAttribute('loading', 'lazy');
      if (!img.hasAttribute('decoding')) img.setAttribute('decoding', 'async');
    }

    // -----------------------------------------------------------------------
    // Smart Logo Handling: conditional priority (mobile vs desktop)
    // -----------------------------------------------------------------
<!-- externalized: moved to theme.css -->
mobileLogo  = document.querySelector('.MenuNewsLogo-img img');

    var preferred
<!-- externalized: moved to theme.css -->
eLogo;

    var hi = (preferred && isVisible(preferred)) ? preferred :

<!-- externalized: moved to theme.css -->
ferred) ? secondary : preferred;

    if (hi) setHigh(hi);
    if (lo) setLow(lo);

    // -----------------------------------------------------------------------
    // Hero/LCP candidate: first visible content image (best-effort)
    // -----------------------------------------------------------------------
    var hero = document.querySelector('.post-body img, .post img, article img, .main-wrapper img, #main img');
    if (hero && isVisible(hero)) {
      if (!hero.hasAttribute('fetchpriority')) hero.setAttribute('fetchpriority', 'high');
      if (!hero.hasAttribute('loading')) hero.setAttribute('loading', 'eager');
      if (!hero.hasAttribute('decoding')) hero.setAttribute('decoding', 'async');
    }

    // -----------------------------------------------------------------------
    // Default: lazy-load remaining images + async decode
    // -----------------------------------------------------------------------
    var imgs = document.images || [];
    for (var i = 0; i < imgs.length; i++) {
      var img = imgs[i];
      if (!img) continue;
      if (!img.hasAttribute('decoding')) img.setAttribute('decoding', 'async');

      // Skip the chosen high-priority images
      if (img === hi || img === hero) continue;

      if (!img.hasAttribute('loading')) img.setAttribute('loading', 'lazy');
    }

    // Lazy-load iframes by default
    var iframes = document.getElementsByTagName('iframe');
    for (var j = 0; j < iframes.length; j++) {
      var fr = iframes[j];
      if (fr && !fr.hasAttribute('loading')) fr.setAttribute('loading', 'lazy');
    }

  } catch(e){console.error(e);}
})();
